public class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length;
        BitSet[] dp = new BitSet[n];
        for (int j = 0; j < n; j++) {
            dp[j] = new BitSet(1001);
            dp[j].set(0);
        }

        boolean f = true;
        for (int num : nums) {
            if (num != 0) {
                f = false;
                break;
            }
        }
        if (f) return 0;

        for (int k = 0; k < queries.length; k++) {
            int l = queries[k][0];
            int r = queries[k][1];
            int v = queries[k][2];

            for (int j = l; j <= r; j++) {
                BitSet sVal = (BitSet) dp[j].clone();
                shiftLeft(sVal, v);
                dp[j].or(sVal);
                for (int pos = nums[j] + 1; pos < 1001; pos++) {
                    dp[j].clear(pos);
                }
            }

            boolean fs = true;
            for (int j = 0; j < n; j++) {
                if (!dp[j].get(nums[j])) {
                    fs = false;
                    break;
                }
            }

            if (fs) return k + 1;
        }

        return -1;
    }

    private void shiftLeft(BitSet bitset, int v) {
        for (int i = 1000 - v; i >= 0; i--) {
            if (bitset.get(i)) {
                bitset.set(i + v);
            } else {
                bitset.clear(i + v);
            }
        }
        for (int i = 0; i < v; i++) {
            bitset.clear(i);
        }
    }
}

