import java.util.*;

class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length;
        BitSet[] dp = new BitSet[n];

        // Initialize BitSet
        for (int j = 0; j < n; j++) {
            dp[j] = new BitSet(1001);
            dp[j].set(0);
        }

        // Check if all numbers are zero
        boolean allZero = true;
        for (int num : nums) {
            if (num != 0) {
                allZero = false;
                break;
            }
        }
        if (allZero) return 0;

        // Process queries
        for (int k = 0; k < queries.length; k++) {
            int l = queries[k][0];
            int r = queries[k][1];
            int v = queries[k][2];

            for (int j = l; j <= r; j++) {
                BitSet shifted = new BitSet(1001);

                // Perform manual left shift operation
                for (int pos = dp[j].nextSetBit(0); pos >= 0 && pos + v < 1001; pos = dp[j].nextSetBit(pos + 1)) {
                    shifted.set(pos + v);
                }

                dp[j].or(shifted);

                // Reset unnecessary bits
                for (int pos = nums[j] + 1; pos < 1001; pos++) {
                    dp[j].clear(pos);
                }
            }

            // Check if all elements are achievable
            boolean allAchieved = true;
            for (int j = 0; j < n; j++) {
                if (!dp[j].get(nums[j])) {
                    allAchieved = false;
                    break;
                }
            }

            if (allAchieved) return k + 1;
        }

        return -1;
    }

    public static void main(String[] args) {
        Solution solution = new Solution();
        int[] nums = {1, 2, 3, 4, 5};
        int[][] queries = {{0, 2, 1}, {1, 4, 2}, {0, 4, 3}};

        int result = solution.minZeroArray(nums, queries);
        System.out.println(result);
    }
}

