class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        ## binary
        def admissible(ind, queries):
            d = []
            for i, query in enumerate(queries):
                l,r,v = query
                if ind>=l and ind<=r:
                    d += [(i,v)]
            return d



        def repeat(num, prev, cur):
            out = prev.copy()
            for p in prev:
                if p+cur <= num:
                    out.add(p+cur)
            return out



        def addable(nums, ind, queries, mid):
            a = admissible(ind, queries)
            # print(ind, a, mid)
            prev = {0}
            num = nums[ind]
            for i, v in a: ### only allow i <= mid
                if i+1 <= mid:
                    next = repeat(num, prev, v)
                    prev = next
                    # print(prev, v, next)
                    if num in next:
                        return True
            return False

        def binarysearch(nums, ind, queries):
            if nums[ind] == 0: return 0
            left = 0 ### always fail
            right = len(queries) + 1 ### always work
            while right - left >= 2:
                mid = (left+right)//2
                # print(ind, mid, addable(nums, ind, queries, mid))
                if addable(nums, ind, queries, mid) == True:
                    right = mid
                else:
                    left = mid
            return right

        out = 0
        for ind in range(len(nums)):
            # print(binarysearch(nums, ind, queries))
            if binarysearch(nums, ind, queries) == len(queries)+1:
                return -1
            out = max(out, binarysearch(nums, ind, queries))
        return out

