from bisect import bisect_left
from collections import defaultdict
from typing import List

class ZC:
    def chk(self, a: List[int]) -> bool:
        return all(x == 0 for x in a)

class BS:
    def srch(self, a: List[int], q: List[List[int]], vld) -> int:
        l, r, rs = 0, len(q), -1
        while l <= r:
            m = (l + r) // 2
            if vld(m):
                rs, r = m, m - 1
            else:
                l = m + 1
        return rs

class QP:
    def get(self, q: List[List[int]], lim: int):
        mp = defaultdict(list)
        j = 0
        while j < lim:
            l, r, v = q[j]
            i = l
            while i <= r:
                mp[i].append(v)
                i += 1
            j += 1
        return mp

class SC:
    def can(self, t: int, v: List[int]) -> bool:
        if t == 0:
            return True
        dp = [False] * (t + 1)
        dp[0] = True
        for x in v:
            j = t
            while j >= x:
                if dp[j - x]:
                    dp[j] = True
                j -= 1
        return dp[t]

class VH:
    def vld(self, a: List[int], q: List[List[int]], p: QP, s: SC):
        return lambda lim: all(s.can(a[i], p.get(q, lim)[i]) for i in range(len(a)))

class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        zc = ZC()
        if zc.chk(nums):
            return 0

        qp = QP()
        sc = SC()
        vh = VH()
        vr = vh.vld(nums, queries, qp, sc)

        bs = BS()
        return bs.srch(nums, queries, vr)

