class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        dp = []
        non_zero = False
        for i, x in enumerate(nums):
            non_zero |= (x != 0)
            dp.append([False] * (x + 1))
            dp[i][0] = True
        if not non_zero:
            return 0
        for k, (l, r, v) in enumerate(queries):
            for i in range(l, r + 1):
                target = nums[i]
                if v > target:
                    continue
                for s in range(target - v, -1, -1):
                    if dp[i][s]:
                        dp[i][s + v] = True
            if all(dp[i][nums[i]] for i in range(n)):
                return k + 1  
        return -1

