class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {

        auto dfs = [&](auto&& dfs, int x, int ind, int y, int z, vector<vector<int>>&dp) -> bool {
            if (x == 0)
                return true;
            if (ind == z)
                return false;
            if(dp[x][ind] != -1)
                    return dp[x][ind];
            if (queries[ind][0] <= y && queries[ind][1] >= y &&
                queries[ind][2] <= x)
                return dp[x][ind] =  (dfs(dfs, x, ind + 1, y, z, dp) ||
                        dfs(dfs, x - queries[ind][2], ind + 1, y, z, dp));
            return dp[x][ind] =  dfs(dfs, x, ind + 1, y, z, dp);
        };
        auto check = [&](int m) -> bool
        {
            bool ans = true;
            for(int i = 0; i < nums.size() && ans; ++i)
                {
                    vector dp(nums[i] + 1, vector(queries.size() + 1, -1));
                    ans &= dfs(dfs, nums[i], 0, i, m, dp);
                }
            return ans;
        };
        int l = 0, r = queries.size();
        int ans = -1;
        while(l <= r)
            {
                int m = (l+r) >> 1ll;
                // cout << m << "n";
                if(check(m)){
                    ans = m;
                    r = m - 1;
                }
                else{
                    l = m + 1;
                }
            }
        return ans;
    }
};

