            next if num_set[j].include?(0)
            if num_set[j].include?(val)
                num_set[j] = Set[0]
                next
            end

            s = Set.new
            num_set[j].each{
                s << _1
                s << _1 - val if _1 > val
            }
            num_set[j] = s
        }

        return i + 1 if num_set.all?{_1.include?(0)}
    }
    -1
