class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int m = queries.size();

        int k = 0;

        for(int i = 0; i < n; i++) {
            set<int> sums = {0};
            if(nums[i] == 0) {
                continue;
            }
            int target = nums[i];

            for(int j = 0; j < m; j++) {
                if(queries[j][0] > i || queries[j][1] < i) {
                    continue;
                }

                vector<int> newS = {};
                for(int sum : sums) {
                    newS.push_back(sum + queries[j][2]);
                }

                for(int sum : newS) {
                    if(sum == target) {
                        k = max(k, j + 1);
                        goto next;
                    }
                    // cout << i << ": " << sum << " inserted into sums" << endl;
                    sums.insert(sum);
                }
            }

            return -1;

            next:
        }

        return k;
    }
};

