class Solution {
public:
    #define vi vector<int>
    #define vvi vector<vector<int>>
    #define pb push_back
    #define sz(x) (int)(x).size()

    bool canSumTab(int t, vi& opt) {
        vector<int> dp(t + 1, 0); 
        dp[0] = 1;
        for (int num : opt) {
            for (int j = t; j >= num; j--) {
                dp[j] = dp[j] || dp[j - num];
            }
        }
        return dp[t];
    }

    bool isPossible(vi& arr, vvi& mod, int lim) {
        vi temp = arr;
        int n = sz(arr);
        vvi ch(n);


        for (int i = 0; i < lim; i++) {
            int l = mod[i][0], r = mod[i][1], v = mod[i][2];
            for (int j = l; j <= r; j++) {
                ch[j].pb(v);
            }
        }

        for (int i = 0; i < n; i++) {
            if (!temp[i]) continue;
            if (!canSumTab(temp[i], ch[i])) return false;
        }
        return true;
    }

    int minZeroArray(vi& arr, vvi& mod) {
        int l = 0, r = sz(mod), ans = -1;
        while (l <= r) {
            int m = l + (r - l) / 2;
            if (isPossible(arr, mod, m)) {
                ans = m;
                r = m - 1;
            } else {
                l = m + 1;
            }
        }
        return ans;
    }
};

