class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length;
        List<List<int[]>> queriesForJ = new ArrayList<>();
        for (int j = 0; j < n; j++) {
            List<int[]> list = new ArrayList<>();
            for (int i = 0; i < queries.length; i++) {
                int l = queries[i][0];
                int r = queries[i][1];
                if (j >= l && j <= r) {
                    int val = queries[i][2];
                    list.add(new int[]{i, val});
                }
            }
            queriesForJ.add(list);
        }

        int maxK = -1;
        for (int j = 0; j < n; j++) {
            int target = nums[j];
            if (target == 0) {
                continue;
            }

            List<int[]> qs = queriesForJ.get(j);
            if (qs.isEmpty()) {
                return -1;
            }

            Map<Integer, Integer> dp = new HashMap<>();
            dp.put(0, -1);

            for (int[] q : qs) {
                int originalIndex = q[0];
                int val = q[1];
                List<Integer> sums = new ArrayList<>(dp.keySet());
                for (int s : sums) {
                    int newSum = s + val;
                    int currentMaxIndex = dp.get(s);
                    int newMaxIndex = Math.max(currentMaxIndex, originalIndex);
                    if (!dp.containsKey(newSum) || newMaxIndex < dp.getOrDefault(newSum, Integer.MAX_VALUE)) {
                        dp.put(newSum, newMaxIndex);
                    }
                }
            }

            if (!dp.containsKey(target)) {
                return -1;
            }

            int k_j = dp.get(target);
            maxK = Math.max(maxK, k_j + 1);
        }

        return maxK == -1 ? 0 : maxK;
    }
}

