public:

    int minZeroArray(vector<int>& arr, vector<vector<int>>& ranges) {
        int length = arr.size(), numQueries = ranges.size();
        int startt = 0, endd = numQueries, result = -1;

        while (startt <= endd) {
            int middle = startt +(-startt + endd) / 2;
            if (canAchieveZero(middle, arr, ranges, length)) {
                result = middle;
                endd = middle - 1;
            } else {
                startt = middle + 1;
            }
        }
        return result;
    }
};

