class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        reached = [num == 0 for num in nums]
        queues = [{num} for num in nums]
        if all(reached): return 0

        for k, (l, r, val) in enumerate(queries):
            for i, num in enumerate(nums):
                if not l <= i <= r or reached[i]:
                    continue
                if val in queues[i]:
                    reached[i] = True
                if not reached[i]:
                    new = set()
                    for num in queues[i]:
                        if val < num:
                            new.add(num - val)
                    queues[i] |= new

            if all(reached):
                return k + 1

        return -1

