        for(int i=0;i<k;i++){
            for(int j=q[i][0];j<=q[i][1];j++){
                sub[j][q[i][2]]++;
            }
        }

        for(int i=0;i<n;i++){
            vector<vector<int>> dp(11, vector<int>(1001, -1));
            if(!rec(dp, sub[i], nums[i], 1)) return false;
        }

        return true;
    }
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int l=0, r=queries.size(), m;
        while(l<=r){
            m = (l+r)>>1;
            if(check(nums, queries, m)) r=m-1;
            else l=m+1;
        }

        return (l==queries.size()+1? -1 : l);
    }
};

