class Solution {
public:
    bool check(long long target, long long idx, vector<vector<int> > &queries, long long k){
        vector<long long> coins;
        if(k == queries.size()+1)
            return false;
        for(int i = 0; i < k; i++){
            if(queries[i][0] <= idx && queries[i][1] >= idx){
                coins.push_back(queries[i][2]);
            }
        }

        vector<bool> dp(target+1);
        dp[0] = true;
        for(int i = 0; i < coins.size(); i++){
            for(int j = target; j - coins[i] >= 0; j--){
                if(dp[j - coins[i]])
                    dp[j] = true;
            }
            if(dp[target])
                return true;
        }
        return false;
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        bool flag = false;
        for(int i = 0; i < nums.size(); i++){
            if(nums[i] != 0){
                flag = true;
                break;
            }
        }
        if(!flag)
            return 0;

        long long ans = INT_MIN;
        for(int i = 0; i < nums.size(); i++){
            long long l = 0, r = queries.size()+1;

            while(l < r){
                long long mid = (l + r) / 2;
                if(check(nums[i], i, queries, mid))
                    r = mid;
                else l = mid+1;
            }

            if(l == queries.size()+1)
                return -1;
            ans = max(ans, l);
        }
        return ans;
    }
};

