class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        def check(idx, max_queries):
            vals = []
            for i in range(max_queries):
                l, r, val = queries[i]
                if l <= idx <= r:
                    vals.append(val)

            target = nums[idx]
            if not vals:
                return target == 0
            dp = {0}
            for v in vals:
                new_dp = dp | {x + v for x in dp}
                dp = new_dp
                if target in dp:
                    return True
            return False

        n = len(nums)
        ans = 0

        for idx in range(n):
            lo, hi = 0, len(queries)
            curr = -1

            while lo <= hi:
                mid = (lo + hi) // 2
                if check(idx, mid):
                    curr = mid
                    hi = mid - 1
                else:
                    lo = mid + 1

            if curr == -1:
                return -1
            ans = max(ans, curr)

        return ans

