import java.util.*;

class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length;
        BitSet[] dp = new BitSet[n];

        // Initialize dp array
        for (int j = 0; j < n; j++) {
            dp[j] = new BitSet(1001);
            dp[j].set(0, true);
        }

        // Check if all elements are zero
        boolean allZero = true;
        for (int num : nums) {
            if (num != 0) {
                allZero = false;
                break;
            }
        }
        if (allZero) return 0;

        // Process queries
        for (int k = 0; k < queries.length; k++) {
            int l = queries[k][0];
            int r = queries[k][1];
            int v = queries[k][2];

            for (int j = l; j <= r; j++) {
                BitSet shifted = (BitSet) dp[j].clone();
                shifted = shiftLeft(shifted, v);
                dp[j].or(shifted);

                for (int pos = nums[j] + 1; pos < 1001; pos++) {
                    dp[j].clear(pos);
                }
            }

            // Check if all nums[j] are achieved
            boolean allAchieved = true;
            for (int j = 0; j < n; j++) {
                if (!dp[j].get(nums[j])) {
                    allAchieved = false;
                    break;
                }
            }

            if (allAchieved) return k + 1;
        }

        return -1;
    }

    // Helper function to shift BitSet left by a given value
    private BitSet shiftLeft(BitSet bs, int shift) {
        BitSet shifted = new BitSet(bs.length());
        for (int i = bs.nextSetBit(0); i >= 0; i = bs.nextSetBit(i + 1)) {
            if (i + shift < 1001) {
                shifted.set(i + shift);
            }
        }
        return shifted;
    }

    public static void main(String[] args) {
        Solution sol = new Solution();
        int[] nums = {1, 2, 3, 4, 5};
        int[][] queries = {
            {0, 2, 1},
            {1, 4, 2}
        };
        System.out.println(sol.minZeroArray(nums, queries)); // Example call
    }
}

