class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int low=-1; int high=queries.length-1; int ans=-1;
        while(low<=high){
            int mid=low+(high-low)/2;
            if(solve(nums,queries,mid)){
                ans=mid+1;
                high=mid-1;
            }
            else low=mid+1;
        }
        return ans;
    }

    public boolean solve(int nums[],int[][] q,int k){
        int n=nums.length; 
        for(int i=0;i<n;i++){
            //by using k queries whether will i make the nums[i] or not 
            //TC: n*k*k*(logK)
            Boolean dp[][]=new Boolean[k+1][nums[i]+1];
            if(!solve(0,k,nums[i],i,q,dp))
            return false;
        }
        return true;
    }

    public boolean solve(int currIdx,int k,int num,int numIdx,int q[][],Boolean dp[][]){
        if(num==0)return true;
        if(currIdx>k)return false;
        if(dp[currIdx][num]!=null)return dp[currIdx][num];
        int l=q[currIdx][0]; int r=q[currIdx][1]; int val=q[currIdx][2];
        boolean flag=solve(currIdx+1,k,num,numIdx,q,dp);
        if(flag)return dp[currIdx][num]=true;
        if(numIdx>=l&&numIdx<=r&&(num-val>=0))flag|=solve(currIdx+1,k,num-val,numIdx,q,dp);
        return dp[currIdx][num]=flag;
    }
}

