class Solution {
public:
    long long minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {

        int maxx = *max_element(nums.begin(), nums.end());
        if(maxx == 0) return 0;

        long long n = nums.size();
        long long Q = queries.size();

        vector<long long> minK(n, -1);

        for(long long i=0; i<n; i++){

            vector<bool> dp(nums[i] + 1, false);
            dp[0] = true;

            for(long long q=0; q<Q; q++){

                if(i < queries[q][0] or i > queries[q][1]) continue;

                long long val = queries[q][2];

                for(long long x=dp.size() - 1; x >= 0; x--){
                    if(x + val < dp.size() and dp[x])
                        dp[x + val] = true; 
                }

                if(dp[nums[i]]){
                    minK[i] = q + 1;
                    break;
                }
            }

            // cout<<minK[i]<<endl;
            if(minK[i] == -1) return -1;
        }

        return *max_element(minK.begin(), minK.end());
    }
};

