class Solution {
public:
    bool isPossible(int x,vector<int> &v){
        if (x == 0) return true;
        vector<bool> dp(x + 1, false);
        dp[0] = true; 
        for (int num : v) {
            for (int i = x; i >= num; i--) {
                if (dp[i - num]) {
                    dp[i] = true;
                }
            }
        }
        return dp[x];
    }
    bool isValid(int k,vector<int> &v,vector<vector<int>> &q){
        vector<vector<int>> dp(v.size());

        for(int i=0;i<k;i++){
            for(int j=q[i][0];j<=q[i][1];j++){
                dp[j].push_back(q[i][2]);
            }
        }

        for(int i=0;i<dp.size();i++){
            if(!isPossible(v[i],dp[i])){
                return false;
            }
        }
        return true;
    }
    int minZeroArray(vector<int>& v, vector<vector<int>>& q) {
        int cnt=0;
        for(int i=0;i<v.size();i++){
            if(v[i]==0) cnt++;
        }
        if(cnt==v.size()) return 0;
        int s=1,e=q.size();
        int ans=-1;
        while(s<=e){
            int mid=s+(e-s)/2;
            if(isValid(mid,v,q)){
                ans=mid;
                e=mid-1;
            }else s=mid+1;
        }
        return ans;
    }
};

