class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int m = queries.size();

        if (reduce(nums.begin(), nums.end(), 0) == 0) return 0;

        int left = 1;
        int right = m;

        auto check = [&](int k) -> bool {
            // [0, k)
            vector<vector<int>> vc(n);
            for (int i = 0; i < k; ++i) {
                int l = queries[i][0], r = queries[i][1], v = queries[i][2];
                for (int j = l; j <= r; ++j) {
                    vc[j].push_back(v);
                }
            }
            // knapsack for every index

            auto knapsack = [&](int index) -> bool {
                int ks = vc[index].size();
                vector<vector<bool>> dp(ks + 1, vector<bool>(nums[index] + 1, false));
                dp[0][0] = true;
                for (int i = 0; i < ks; ++i) {
                    dp[i + 1][0] = true;
                    for (int j = 0; j < nums[index]; ++j) {
                        dp[i + 1][j + 1] = dp[i][j + 1];
                        if (vc[index][i] <= j + 1) dp[i + 1][j + 1] = dp[i + 1][j + 1] || dp[i][j + 1 - vc[index][i]];
                    }
                }
                return dp[ks][nums[index]];
            };
            for (int i = 0; i < n; ++i) {
                if (nums[i] == 0) continue;
                if (!knapsack(i)) return false;
            }
            return true;
        };

        while (left <= right) {
            int mid = (left + right) / 2;
            if (check(mid)) right = mid - 1;
            else left = mid + 1;
        }
        if (left == m + 1) return -1;
        return left;
    }
};

