class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int m = queries.size();
        vector<int> arr(n, m + 1); 

        for (int i = 0; i < n; ++i) {
            int target = nums[i];
            if (target == 0) {
                arr[i] = 0;
                continue;
            }
            vector<bool> dp(target + 1, false);
            dp[0] = true;
            arr[i] = m + 1; 

            for (int j = 0; j < m; ++j) {
                auto& q = queries[j];
                int l = q[0], r = q[1], val = q[2];
                if (i < l || i > r) {
                    continue;
                }

                vector<bool> new_dp = dp; 
                for (int s = 0; s <= target - val; ++s) {
                    if (dp[s]) {
                        new_dp[s + val] = true;
                    }
                }
                dp = new_dp; 

                if (dp[target]) {
                    if (j + 1 < arr[i]) {
                        arr[i] = j + 1;
                    }
                }
            }

            if (!dp[target]) {
                return -1;
            }
        }

        int result = *max_element(arr.begin(), arr.end());
        return result <= m ? result : -1;
    }
};

