class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length, q = queries.length;
        int low = 0; int high = q;
        int res = -1;
        while(low<=high){
            int mid = (low+high)/2;
            if(isPos(mid,nums,queries,n,q)){
                res = mid;
                high = mid-1;
            }else{
                low = mid+1;
            }
        }
        return res;
    }

    boolean isPos(int k, int nums[], int queries[][], int n, int q){
        List<List<Integer>> list = new ArrayList<>();
        for(int i=0; i<n; i++){
            list.add(new ArrayList<>());
        }
        for(int i=0; i<k; i++){
            for(int j=queries[i][0]; j<=queries[i][1]; j++){
                list.get(j).add(queries[i][2]);
            }
        }
        boolean flag = true;
        for(int i=0; i<n; i++){
            if(nums[i]==0) continue;
            if(!isSum(list.get(i),nums[i],0)){
                flag = false;
            }
        }
        return flag;
    }

    boolean isSum(List<Integer> list, int val, int idx) {
        boolean[] dp = new boolean[val + 1];
        dp[0] = true;

        for (int num : list) {
            for (int j = val; j >= num; j--) {
                dp[j] |= dp[j - num];
            }
        }

        return dp[val];
    }
}

