class Solution {
public:
    bool helper(int x, vector<int>& nums, vector<vector<int>>& queries) {
        vector<vector<int>> v(nums.size());
        for (int i=0;i<x;i++) {
            auto &it=queries[i];
            for (int j = it[0]; j <= it[1]; j++)
                v[j].push_back(it[2]);
        }

        for (int i = 0; i < nums.size(); i++) {
            vector<int> dp(nums[i] + 1, 0);
            dp[0] = 1;
            int maxi = nums[i];
            for (auto it : v[i]) {
                for (int j = maxi - it; j >= 0; j--) {
                    dp[j + it] = max(dp[j], dp[j + it]);
                }
            }
            if (dp[nums[i]] == 0)
                return false;
        }
        return true;
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int l=0, r=queries.size();
        while(r-l>1)
            {
                int mid=(l+r)/2;
                if(helper(mid, nums, queries))
                    r=mid;
                else
                    l=mid;
            }

        if(helper(l, nums, queries))
            return l;
        else if(helper(r, nums, queries))
            return r;
        else
            return -1;
    }
};

