class Solution {
    int MAX;
    private int helper(int sum,int i,int[][] dp,List<int[]> a){
        if(sum==0)return 0;
        if(i>=a.size())return MAX;
        if(dp[sum][i]!=-1)return dp[sum][i];

        if(sum-a.get(i)[0]<0){
            return dp[sum][i] = helper(sum,i+1,dp,a);
        }
        if(sum-a.get(i)[0]==0){
            return dp[sum][i] = a.get(i)[1];
        }

        return dp[sum][i] = Math.min(helper(sum-a.get(i)[0],i+1,dp,a),helper(sum,i+1,dp,a));
    }
    public int minZeroArray(int[] nums, int[][] q) {
        MAX = q.length+1;
        List<List<int[]>> l = new ArrayList<>();

        int n = nums.length;
        for(int i=0;i<n;i++){
            l.add(new ArrayList<>());
        }

        int k = 0;
        for(int[] qq:q){
            for(int j=qq[0];j<=qq[1];j++){
                l.get(j).add(new int[]{qq[2],k+1});
            }
            k++;
        }

        // for(int i=0;i<n;i++){
        //     for(int[] v:l.get(i)){
        //         System.out.print("["+v[0]+" "+v[1]+"]");
        //     }
        //     System.out.println();
        // }
        int ans = -1;
        boolean f = true;
        for(int i=0;i<n;i++){
            int[][] dp = new int[nums[i]+2][l.get(i).size()];
            for(int j=0;j<=nums[i];j++){
                Arrays.fill(dp[j],-1);
            }

            int v = helper(nums[i],0,dp,l.get(i));
            // System.out.println(v);
            if(v==MAX){
                f = false;
                break;
            }

            ans = Math.max(ans,v);
        }
        if(!f)return -1;

        return ans;
    }
}

