class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int[][] arr = new int[nums.length][queries.length];
        int max = 0;
        for (int i = 0; i < nums.length; i++) {
            for (int j = 0; j < queries.length; j++) {
                if (i >= queries[j][0] && i <= queries[j][1]) {
                    arr[i][j] = queries[j][2];
                }
            }

            int[][] dp = new int[nums[i] + 1][queries.length];
            for (int j = 1; j <= nums[i]; j++) {
                for (int k = queries.length - 1; k >= 0; k--) {
                    if (arr[i][k] == j) {
                        dp[j][k] = k + 1;
                        continue;
                    } 
                    if (k == queries.length - 1) {
                        dp[j][k] = Integer.MAX_VALUE;
                        continue;
                    }
                    if (arr[i][k] < j) {
                        dp[j][k] = Math.min(dp[j - arr[i][k]][k + 1], dp[j][k + 1]);
                    } else {
                        dp[j][k] = dp[j][k + 1];
                    }
                }
            }
            max = Math.max(max, dp[nums[i]][0]);
        }

        return max == Integer.MAX_VALUE ? -1 : max;
    }
}

