class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int MAX = *std::max_element(nums.begin(), nums.end());
        if (MAX == 0) return 0;
        std::vector<std::vector<int> > dp(n, std::vector<int>(MAX + 1, 0));

        for (int i = 0; i < n; i++) {
            dp[i][nums[i]] = 1;
        }
        for (int k = 0; k < queries.size(); k++) {
            int l = queries[k][0];
            int r = queries[k][1];
            int val = queries[k][2];
            for (int i = l; i <= r; i++) {
                for (int j = 0; j < MAX + 1; j++) {
                    dp[i][j] |= (j + val < MAX + 1 ? dp[i][j + val] : 0);
                }
            }
            bool ok = true;
            for (int i = 0; i < n; i++) {
                ok &= dp[i][0];
            }
            if (ok) {
                return k + 1;
            }
        }
        return -1;
    }
};

