class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        if (!check(nums, queries, queries.size())) return -1;

        int l = 0, r = queries.size();
        while (l < r) {
            int m = l + (r - l) / 2;
            if (check(nums, queries, m)) r = m;
            else l = m + 1;
        }
        return l;
    }
private:
    bool check(vector<int>& nums, vector<vector<int>>& queries, int k) {
        vector<vector<int>> values(nums.size());
        for (int i = 0; i < k; i++) {
            int& l = queries[i][0];
            int& r = queries[i][1];
            int& val = queries[i][2];
            for (int j = l; j <= r; j++) values[j].push_back(val);
        }

        for (int i = 0; i < nums.size(); i++) {
            if (!makeZero(nums[i], values[i])) return false;
        }
        return true;
    }
    bool makeZero(int num, vector<int>& vals) {
        vector<bool> dp(num + 1, false);
        dp[0] = true;
        for (auto& val : vals) {
            for (int j = num; (j - val) >= 0; j--) {
                dp[j] = dp[j] || dp[j - val];
            }
        }
        return dp[num];
    }
};

