class Solution {
public:
    bool isValid(vector<int>& nums, int n, vector<vector<int>>& queries, int k) {
        for (int i = 0; i < n; i++) {
            int val = nums[i];
            vector<int> v;
            for (int j = 0; j < k; j++) {
                if (queries[j][0] <= i && queries[j][1] >= i)
                    v.push_back(queries[j][2]);
            }
            int sum_available = 0;
            for (int x : v)
                sum_available += x;
            if (sum_available < val)
                return false;

            vector<int> dp(val + 1, 0);
            dp[0] = 1;
            for (int j = 0; j < v.size(); j++) {
                for (int t = val; t >= v[j]; t--) {
                    dp[t] = dp[t] || dp[t - v[j]];
                }
            }
            if (!dp[val])
                return false;
        }
        return true;
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int q = queries.size();
        int low = 0, high = q, res = -1;
        while (low <= high) {
            int mid = low + (high - low) / 2;
            if (isValid(nums, nums.size(), queries, mid)) {
                res = mid;
                high = mid - 1;
            } else {
                low = mid + 1;
            }
        }
        return res;
    }
};

