class Solution {
    bool subsetSum(vector<int>& values, int target) {
        int n = values.size();
        vector<bool> dp(target + 1, false);
        dp[0] = true; 

        for (int val : values) {
            for (int j = target; j >= val; j--) {
                dp[j] = dp[j] || dp[j - val];
            }
        }
        return dp[target];
    }

    bool check(int k, vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        unordered_map<int, vector<int>> mp; 

        for (int i = 0; i < k; i++) {
            int l = queries[i][0], h = queries[i][1], val = queries[i][2];
            for (int j = l; j <= h; j++) {
                mp[j].push_back(val);
            }
        }

        for (int i = 0; i < n; i++) {
            if (nums[i] > 0) {
                if (mp[i].empty() || !subsetSum(mp[i], nums[i])) {
                    return false; 
                }
            }
        }
        return true;
    }

public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int l = 0, h = queries.size(), ans = -1;

        while (l <= h) {
            int mid = l + (h - l) / 2;
            if (check(mid, nums, queries)) {
                ans = mid;
                h = mid - 1; 
            } else {
                l = mid + 1;
            }
        }
        return ans;
    }
};

