class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        vector<unordered_set<int>> dps(nums.size());

        auto isGood = [&dps]() {
            for (const auto& s : dps) {
                if (!s.contains(0))
                    return false;
            }

            return true;
        };

        for (auto i = 0; i < nums.size(); ++i) {
            dps[i].emplace(nums[i]);
        }

        if (isGood())
            return 0;

        auto result = 0;
        for (const auto& query : queries) {
            auto start = query[0];
            auto end = query[1];
            auto val = query[2];

            for (auto i = start; i <= end; ++i) {
                auto clone = dps[i];
                for (auto elem : clone) {
                    if (elem - val >= 0) {
                        dps[i].emplace(elem - val);
                    }
                }
            }

            result++;
            if (isGood()) {
                return result;
            }
        }

        return -1;
    }
};

