class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        if max(nums) == 0: return 0
        n = len(nums)
        dp = [set([0]) for _ in range(n)]
        for k, (l, r, val) in enumerate(queries):
            for i in range(l, r+1):
                cur = set()
                for sum in dp[i]:
                    if sum + val <= nums[i]:
                        cur.add(sum + val)
                dp[i].update(cur)
            is_valid = all(nums[i] in dp[i] for i in range(n))
            if is_valid:
                return k + 1
        return -1

