        boolean allZeros = true;
        for (int index = 0; index < nums.length; ++index) {
            if (nums[index] != 0) {
                allZeros = false;
                break;
            }
        }

        if (allZeros) {
            return 0;
        }

        ArrayList<HashSet<Integer>> possibleValues = new ArrayList<>();
        for (int index = 0; index < nums.length; ++index) {
            HashSet<Integer> hashSetToAdd = new HashSet<>();
            hashSetToAdd.add(nums[index]);
            possibleValues.add(hashSetToAdd);
        }

        for (int queryIndex = 0; queryIndex < queries.length; ++queryIndex) {
            int[] query = queries[queryIndex];
            for (int index = query[0]; index <= query[1]; ++index) {
                HashSet<Integer> valuesToAdd = new HashSet<>();
                for (int key : possibleValues.get(index)) {
                    if (!possibleValues.get(index).contains(key - query[2]) && key - query[2] >= 0) {
                        valuesToAdd.add(key - query[2]);
                    }
                }

                possibleValues.get(index).addAll(valuesToAdd);
            }

            boolean canAllBeZeros = true;
            for (int index = 0; index < nums.length; ++index) {
                if (!possibleValues.get(index).contains(0)) {
                    canAllBeZeros = false;
                    break;
                }
            }

            if (canAllBeZeros) {
                return queryIndex + 1;
            }
        }

        return -1;
    }

    private boolean isAllZerosArray(int nums[]) {
        for (int num : nums) {
            if (num != 0) {
                return false;
            }
        }

        return true;
    }
