class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int ans = 0;
        int m = 0;
        for (int a: nums) m = max(m, a);
        if (m == 0) return 0;
        m++;
        for (int i = 0; i < nums.size(); i++) {
            vector<bool> v(m, false);
            v[nums[i]] = true;
            int k = 0;
            for (; k < queries.size(); k++) {
                vector<int> q = queries[k];
                if (!(i >= q[0] && i <= q[1])) continue;
                vector<bool> t(v.begin(), v.end());
                for (int j = 0; j < m; j++) {
                    if (j - q[2] >= 0 && v[j]) t[j - q[2]] = true;
                }
                v = t;
                if (v[0]) break;
            }
            if (k < queries.size()) ans = max(ans, k + 1);
            else if (!v[0]) return -1;
        }
        return ans;
    }
};

