class Solution {
public:
    int rec(int i , int j , int x ,
vector <vector <int>> &queries , vector <vector <vector <int>>> &dp){
        int q = queries.size();
        if(x==0) return i;
        if(i==q){
            return 1e9;
        }

        if(dp[i][j][x]!= -1 ) return dp[i][j][x];

        int left = queries[i][0],
        right = queries[i][1] , val = queries[i][2];

        int ans = rec(i+1,j,x,queries,dp);
        if((left<=j && j<=right) && x>=val) 
            ans=min(ans,rec(i+1,j,x-val,queries,dp));

        return dp[i][j][x] = ans;
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size() , q = queries.size();
        int m = *max_element(nums.begin(),nums.end());
        vector <vector <vector <int>>> dp(q+1,
        vector <vector <int>> (n+1, vector <int> (m+1,-1)));

        int mn = 0;
        for(int j = 0; j<n; j++){
            mn = max(mn,rec(0,j,nums[j],queries,dp));
        }
        if(mn==1e9) return -1;
        return mn;
    }
};

