class Solution {
public:
    // Function to check if any subset of 'elements' can sum to 'target'
    bool canFormSubsetSum(vector<int>& elements, int target) {
        int m = elements.size();
        vector<bool> dp(target + 1, false);
        dp[0] = true; // Base case: subset sum 0 is always possible

        for (int num : elements) {
            for (int j = target; j >= num; j--) {
                if (dp[j - num]) {
                    dp[j] = true;
                }
            }
        }
        return dp[target];
    }

    // Function to check if the first 'mid' queries can zero out nums
    bool check(int mid, vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        for (int i = 0; i < n; i++) {
            int elem = nums[i];
            vector<int> elements;

            // Collect valid decrements from the first 'mid' queries
            for (int j = 0; j < mid; j++) {
                if (i >= queries[j][0] && i <= queries[j][1]) {
                    elements.push_back(queries[j][2]);
                }
            }

            // Use DP to check if a subset sum can reach 'elem'
            if (!canFormSubsetSum(elements, elem)) {
                return false; // If no valid subset exists, return false
            }
        }
        return true;
    }

    // Binary search to find the minimum number of queries needed
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int low = 0, high = queries.size(), ans = -1;
        while (low <= high) {
            int mid = (low + high) / 2;
            if (check(mid, nums, queries)) {
                ans = mid;
                high = mid - 1; // Try to minimize queries
            } else {
                low = mid + 1;
            }
        }
        return ans;
    }
};

