class Solution {
public:

    bool isSum(int sum, vector<int> &arr) {
        int n = arr.size();
        vector<vector<bool>> dp(n + 1, vector<bool>(sum + 1, false));
        for (int i = 0; i <= n; i++)
            dp[i][0] = true;
        for (int i = 1; i <= n; i++) {

            for (int j = 1; j <= sum; j++) {
                if (j < arr[i - 1]) {
                    dp[i][j] = dp[i - 1][j]; 
                }
                else {
                    dp[i][j] = dp[i - 1][j] 
                    || dp[i - 1][j - arr[i - 1]];
                }
            }
        }
        return dp[n][sum];
    }

    bool chk(int k, vector<int>& nums, vector<vector<int>>& q) {
        int n = nums.size(), sum = 0;
        vector<vector<int>> temp(n + 1);

        for (int i = 0; i < k; i++) {
            int l = q[i][0], r = q[i][1];
            for (int j = l; j <= r; j++) {
                temp[j].push_back(q[i][2]);
            }
        }

        for (int i = 0; i < n; i++) {
            int sum = nums[i];
            vector<int> & t = temp[i];
            if (!isSum(sum, t)) return false;
        }        
        return true;
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int l = 0, r = queries.size(), ans = -1;
        while (l <= r) {
            int mid = l + (r - l) / 2;
            if (chk(mid, nums, queries)) {
                ans = mid;
                r = mid - 1;
            } else {
                l = mid + 1;
            }
        }
        return ans;
    }
};

