class Solution {
public:
    bool isPossible(int k, vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        for (int i = 0; i < n; ++i) {
            int target = nums[i];
            vector<int> vals;
            for (int j = 0; j < k; ++j) {
                if (queries[j][0] <= i && i <= queries[j][1])
                    vals.push_back(queries[j][2]);
            }
            vector<bool> dp(target + 1, false);
            dp[0] = true;
            for (int v : vals) {
                for (int s = target; s >= v; --s) {
                    dp[s] = dp[s] || dp[s - v];
                }
            }
            if (!dp[target]) return false;
        }
        return true;
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int lo = 0, hi = queries.size(), ans = -1;
        while (lo <= hi) {
            int mid = (lo + hi) / 2;
            if (isPossible(mid, nums, queries)) {
                ans = mid;
                hi = mid - 1;
            } else {
                lo = mid + 1;
            }
        }
        return ans;
    }
};

