import java.util.*;

class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length;
        int m = queries.length;
        List<List<int[]>> indexQueries = new ArrayList<>();

        for (int i = 0; i < n; i++) {
            indexQueries.add(new ArrayList<>());
        }

        // Preprocess: for each query, add it to the list of each index it covers
        for (int j = 0; j < m; j++) {
            int l = queries[j][0];
            int r = queries[j][1];
            int val = queries[j][2];
            for (int i = l; i <= r; i++) {
                indexQueries.get(i).add(new int[]{j, val});
            }
        }

        int maxK = 0;

        for (int i = 0; i < n; i++) {
            int target = nums[i];
            if (target == 0) continue;

            List<int[]> qi = indexQueries.get(i);
            if (qi.isEmpty()) return -1;

            // Map to track sum: key is sum, value is [steps, latest_j]
            Map<Integer, int[]> possibleSums = new HashMap<>();
            possibleSums.put(0, new int[]{0, -1}); // Initial state: sum 0 requires 0 steps and no query

            int minK_i = Integer.MAX_VALUE;

            for (int[] entry : qi) {
                int original_j = entry[0];
                int val = entry[1];

                Map<Integer, int[]> temp = new HashMap<>();

                for (Map.Entry<Integer, int[]> e : possibleSums.entrySet()) {
                    int s = e.getKey();
                    int steps = e.getValue()[0];
                    int latest_j = e.getValue()[1];

                    // Option 1: Take this query
                    int newSum = s + val;
                    int newSteps = steps + 1;
                    int newLatestJ = original_j;

                    if (!temp.containsKey(newSum) || 
                        newSteps < temp.get(newSum)[0] || 
                        (newSteps == temp.get(newSum)[0] && newLatestJ < temp.get(newSum)[1])) {
                        temp.put(newSum, new int[]{newSteps, newLatestJ});
                    }

                    // Option 2: Do not take this query (carry forward existing sum)
                    if (!temp.containsKey(s) || 
                        steps < temp.get(s)[0] || 
                        (steps == temp.get(s)[0] && latest_j < temp.get(s)[1])) {
                        temp.put(s, new int[]{steps, latest_j});
                    }
                }

                possibleSums = new HashMap<>(temp);

                // Check if target is reached and update minK_i
                if (possibleSums.containsKey(target)) {
                    int[] current = possibleSums.get(target);
                    int currentK = current[1] + 1;
                    if (currentK < minK_i) {
                        minK_i = currentK;
                    }
                }
            }

            // Check after processing all queries for this index
            if (!possibleSums.containsKey(target)) {
                return -1;
            }

            int[] finalEntry = possibleSums.get(target);
            int finalK = finalEntry[1] + 1;
            if (finalK < minK_i) {
                minK_i = finalK;
            }

            maxK = Math.max(maxK, minK_i);
        }

        return maxK;
    }
}

