    ret = 0
    for i in range(n):
      num = nums[i]
      dp = [[False for _ in range(num + 1)] for _ in range(n_queries + 1)]
      dp[0][num] = True
      if dp[0][0]:
        continue

      for length in range(1, n_queries + 1):
        l, r, val = queries[length - 1]
        for remains in range(0, num + 1):
          dp[length][remains] = dp[length - 1][remains] or (dp[length - 1][remains + val] if l <= i <= r and remains + val <= num else False)
        if dp[length][0]:
          ret = max(ret, length)
          #print(i, dp)
          break
      else:
        ret = -1
        #print(i, dp)
        break
    return ret

