            for ($j = $l; $j <= $r; $j++) {
                for ($pos = 1000; $pos >= $v; $pos--) {
                    $dp[$j][$pos] |= $dp[$j][$pos - $v];
                }
                for ($pos = $nums[$j] + 1; $pos < 1001; $pos++) {
                    $dp[$j][$pos] = false;
