class Solution {
public:
    int memo[1001][1001];

    int dfs(vector<int>& sequence, int cur_idx, int target) {
        if(target == 0)
            return cur_idx;

        if(cur_idx == sequence.size())
            return INT_MAX;

        if(memo[cur_idx][target] != -1)
            return memo[cur_idx][target];

        // choose or not choose
        int ans = INT_MAX;

        // choose
        if(target >= sequence[cur_idx])
            ans = min(ans, dfs(sequence, cur_idx+1, target-sequence[cur_idx]));

        ans = min(ans, dfs(sequence, cur_idx+1, target));

        return memo[cur_idx][target] = ans;
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size(), q = queries.size();

        vector<bool> is_zero(n, false);
        vector<vector<int>> possible_add(n, vector<int>());

        // Note : ans should be i+1
        for(int i = 0; i < q; i++) {
            int l = queries[i][0], r = queries[i][1], val = queries[i][2];

            for(int j = 0; j < n; j++) {
                if(l <= j && j <= r)
                    possible_add[j].push_back(val);
                else
                    possible_add[j].push_back(0);
            }
        }

        int ans = 0;
        for(int i = 0; i < n; i++) {
            memset(memo, -1, sizeof(memo));

            int min_q = dfs(possible_add[i], 0, nums[i]);

            //cout << min_q << endl;

            if(min_q == INT_MAX)
                return -1;

            ans = max(ans, min_q);

        }

        return ans; 
    }
};

