import java.util.*;
class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length, result = -1;
        int low = 0, high = queries.length;

        while (low <= high) {
            int mid = low + ((high - low)/2);

            List<Integer> adj[] = new List[n];

            for (int i = 0; i < n; i++) {
                adj[i] = new ArrayList<>();
            }

            for (int i = 0; i < mid; i++) {
                for (int j = queries[i][0]; j<= queries[i][1]; j++) {
                    adj[j].add(queries[i][2]);
                }
            }

            boolean valid = true;

            for (int i = 0; i < n; i++) {
                if (!isPossible(adj[i], nums[i])) {
                    valid = false;
                    break;
                }
            }

            if (valid) {
                result = mid;
                high = mid - 1;
            } else {
                low = mid + 1;
            }
        }


        return result;
    }

    public boolean isPossible(List<Integer> indices, int val) {
        if (val == 0) {
            return true;
        } else if (indices.size() == 0) {
            return false;
        }

        int n = indices.size();

        boolean dp[][] = new boolean[n + 1][val + 1];

        for (int i = 1; i <= val; i++) {
            dp[0][i] = false;
        }

        for (int i = 0; i <= n; i++) {
            dp[i][0] = true;
        }

        for (int i = 1; i <= n; i++) {
            for (int j = 0; j <= val; j++) {
                dp[i][j] = dp[i - 1][j];

                if (j >= indices.get(i - 1)) {
                    dp[i][j] = dp[i][j] || dp[i - 1][j - indices.get(i - 1)];
                }
            }
        }

        return dp[n][val];
    }
}

