            bool tab[1001] = { false };
            tab[0] = true;
            for (y = 0; y < md; y++) {
                int li = qry[y][0], ri = qry[y][1], va = qry[y][2];
                if (x < li || x > ri) continue;
                for (z = tgt; z >= va; z--) {
