import numpy as np

class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        def can_make(target: int, coins: dict):
            able_to_make = [False] * (target + 1)
            able_to_make[0] = True
            for k, v in coins.items():
                for starting in range(target, -1, -1):
                    if not able_to_make[starting]:
                        continue
                    for n_used in range(v + 1):
                        result = starting + k * n_used
                        if result <= target:
                            able_to_make[result] = True

            #print(f"can_make({target=}, {coins=}) = {able_to_make[target]}")

            return able_to_make[target]

        coins_array = [Counter() for _ in range(n)]
        def is_good(i):
            return can_make(nums[i], coins_array[i])
        def good_indices():
            return [is_good(i) for i in range(n)]
        def all_good():
            return all(is_good(i) for i in range(n))

        if all(num == 0 for num in nums):
            return 0
        for i, (l, r, v) in enumerate(queries):
            for j in range(l, r + 1):
                coins_array[j][v] += 1
            if all_good():
                return i + 1

            #print(f"After {i=}, {good_indices()=}")

        return -1

