public class Solution {
    const int N = 1001;

    public int MinZeroArray(int[] nums, int[][] queries) {
        int nz = nums.Count(n => n > 0);

        if (nz == 0) return 0;

        int n = nums.Length;
        bool[,] ns = new bool[n, N];
        for (int i = 0; i < n; i++) {
            ns[i, nums[i]] = true;
        }

        for (int k = 0; k < queries.Length; k++) {
            int l = queries[k][0];
            int r1 = queries[k][1] + 1;
            int v = queries[k][2];

            for (int i = l; i < r1; i++) {
                if (ns[i, 0]) continue;

                if (ns[i, v]) {
                    ns[i, 0] = true;
                    nz--;
                    if (nz == 0) return k + 1;
                } else {
                    for (int j = v + 1; j < N; j++) {
                        if (ns[i, j]) {
                            ns[i, j - v] = true;
                        }
                    }
                }
            }
        }

        return -1;
    }
}

