import java.util.HashMap;
import java.util.Map;

class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length;
        int m = queries.length;

        // Initialize a DP map for each element to track possible sums and their latest query index
        Map<Integer, Integer>[] dp = new Map[n];
        for (int i = 0; i < n; i++) {
            dp[i] = new HashMap<>();
            dp[i].put(0, -1); // Initial state: sum 0 with no queries used
        }

        int minK = Integer.MAX_VALUE;

        for (int j = 0; j < m; j++) {
            int[] q = queries[j];
            int l = q[0], r = q[1], val = q[2];

            // Update the DP maps for all elements in the current query's range
            for (int i = l; i <= r; i++) {
                if (nums[i] == 0) continue;

                Map<Integer, Integer> newDp = new HashMap<>(dp[i]);
                for (Map.Entry<Integer, Integer> entry : dp[i].entrySet()) {
                    int s = entry.getKey();
                    int currentMaxJ = entry.getValue();
                    int newS = s + val;

                    if (newS > nums[i]) continue; // Exceeding the target sum is not useful

                    int newMaxJ = Math.max(currentMaxJ, j);
                    if (!newDp.containsKey(newS) || newDp.get(newS) < newMaxJ) {
                        newDp.put(newS, newMaxJ);
                    }
                }
                dp[i] = newDp;
            }

            // Check if all elements can be turned to zero with the current queries processed
            boolean allAchieved = true;
            int currentMaxCandidate = 0;
            for (int i = 0; i < n; i++) {
                if (nums[i] == 0) continue;

                if (!dp[i].containsKey(nums[i])) {
                    allAchieved = false;
                    break;
                }
                int ti = dp[i].get(nums[i]);
                currentMaxCandidate = Math.max(currentMaxCandidate, ti + 1);
            }

            if (allAchieved && currentMaxCandidate <= j + 1) {
                minK = Math.min(minK, currentMaxCandidate);
            }
        }

        return minK == Integer.MAX_VALUE ? -1 : minK;
    }
}

