public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int lo=0,hi=queries.size();
        int n=nums.size(),res=-1;
        vector memo(n,vector<vector<int>>(hi,vector<int>(1001,-1)));
        function<int(int,int,int)>dp=[&](int i,int j,int num){
            if(j<0) return num==0?1:0;
            if(memo[i][j][num]!=-1) return memo[i][j][num];
            int ans=dp(i,j-1,num);
            int l=queries[j][0],r=queries[j][1],val=queries[j][2];
            if(l<=i && i<=r && val<=num){
                ans|=dp(i,j-1,num-val);
            }
            return memo[i][j][num]=ans;
        };
        while(lo<=hi){
            int k=(lo+hi)/2;
            bool ok=true;
            for(int i=0;i<n;i++){
                if(!dp(i,k-1,nums[i])){
                    ok=false;
                    break;
                }
            }
            if(ok){
                res=k;
                hi=k-1;
            }
            else lo=k+1;
        }
        return res;
    }
};

