class Solution {
public:
    bool isSubsetSumPossible(vector<int>& nums, int target) {
        int n= nums.size();

        vector<vector<int>> dp(n+1,vector<int>(target+1));
        dp[0][0]=true;
        for(int i=1;i<=n;i++){
            for(int j=0;j<=target;j++){
                if(j>=nums[i-1]){
                    dp[i][j]= dp[i-1][j-nums[i-1]] or dp[i-1][j];
                }else{
                    dp[i][j]= dp[i-1][j];
                }
            }
        }

        return dp[n][target];
    }

