    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n= nums.size();

        int flag=1;
        for(int i=0;i<n;i++){ //already all zero
            if(nums[i]!=0){
                flag=0;
                break;
            }
        }

        if(flag) return 0;

        int s= 0;
        int e= queries.size()-1;
        int ans= -2;

        while(s<=e){
            int mid= (s+e)/2;
            if(isPossible(mid,nums,queries)){
                ans= mid;
                e= mid-1;
            }else{
                s= mid+1;
            }
        }

        return ans+1;
    }
