    private boolean canZero(int[] bottle, int[][] curtain, int pillow) {
        int blanketSize = bottle.length;
        for (int i = 0; i < blanketSize; i++) {
            if (bottle[i] == 0) continue;

            ArrayList<Integer> candidates = new ArrayList<>();
            for (int j = 0; j < pillow; j++) {
